import mathimport numpy# Definicija funkcijedef RazdaljaSmerniKot(x_1,y_1,x_2,y_2):    D = math.sqrt((x_2-x_1)**2+(y_2-y_1)**2)    del_y = y_2-y_1    del_x = x_2-y_1    V = numpy.arctan((y_2-y_1)/(x_2-y_1))    if del_y >= 0 and del_x >= 0:        V_pravi = V    elif del_y <= 0 and del_x <= 0:        V_pravi = V + math.pi    elif del_y <= 0 and del_x >= 0:        V_pravi = V + 2 * math.pi    elif del_y >= 0 and del_x <= 0:        V_pravi = V + math.pi    else:        print("Napaka pri vnašanju koordinat")    print("Razdalja med točkama je", D)    print("Smerni kot med točkama je", math.degrees(V_pravi))# Vnos podatkov v funkcijox_1 = float(input("Podaj x koordinato 1. točke:"))y_1 = float(input("Podaj y koordinato 1. točke:"))x_2 = float(input("Podaj x koordinato 2. točke:"))y_2 = float(input("Podaj y koordinato 2. točke:"))# Klic funkcijeRazdaljaSmerniKot(x_1,y_1,x_2,y_2)